sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '' || newValue == oldValue) {
		return;
	}

	var getEmailAddr = new GlideAjax('GetEmailAddress');   //name of script include
	getEmailAddr.addParam('sysparm_name', 'getEmail');     //name of function 
	getEmailAddr.addParam('sysparm_userID', g_form.getValue('u_requested_for'));   //only param we need

	getEmailAddr.getXML(populateEmailField);

	//callback when response comes back:
	function populateEmailField(response){
		var emailFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");  //'answer' is value of what method returns?
		g_form.clearValue('u_requested_for_email');
		g_form.setValue('u_requested_for_email', emailFromScriptInclude);
		//jslog('Callback complete: ANSWER was ' + emailFromScriptInclude);
	}	
}
